mapper.select:
SELECT b.id, b.title, b.created_at, u.nm FROM board b INNER JOIN user u ON b.user_id = u.id
WHERE b.title LIKE '%${searchText}%' OR b.contents LIKE '%${searchText}%'
ORDER BY b.id DESC LIMIT #{startIdx}, #{size}

mapper.delete:
DELETE FROM board
WHERE id = #{id}
  AND user_id = #{signedUserId}

mapper.update:
UPDATE board
SET
title = #{title},
contents = #{contents}
WHERE id = #{id}
AND user_id = #{signedUserId}
